frappe.ready(() => {
  function generateCaptcha() {
    const num1 = Math.floor(Math.random() * 9) + 1;
    const num2 = Math.floor(Math.random() * 9) + 1;
    const operators = ['+', '-', '*'];
    const operator = operators[Math.floor(Math.random() * operators.length)];

    const question = `${num1} ${operator} ${num2}`;
    let answer;

    switch (operator) {
      case '+': answer = num1 + num2; break;
      case '-': answer = num1 - num2; break;
      case '*': answer = num1 * num2; break;
    }

    // Save answer
    sessionStorage.setItem('correct_captcha', answer);

    const tryInject = () => {
      const captchaEl = document.querySelector('[data-fieldname="custom_captcha"] input');
      if (captchaEl) {
        captchaEl.value = `What is ${question}?`;
        captchaEl.readOnly = true;
      } else {
        setTimeout(tryInject, 200);
      }
    };

    tryInject();
  }

  generateCaptcha();

  // Intercept submit button click
  document.querySelector('button[type="submit"]').addEventListener('click', function (e) {
    const correct = sessionStorage.getItem('correct_captcha');
    const userAnswer = frappe.web_form.get_value('custom_your_answer');

    if (!userAnswer || parseInt(userAnswer) !== parseInt(correct)) {
      e.preventDefault(); // üö´ Stop submission
      frappe.msgprint('‚ùå Incorrect or missing CAPTCHA. Please solve it correctly.');
    }
  });
});

